import { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

export default function Posts(props) {
  const [posts, setPosts] = useState([]);
  const { postId } = useParams();
  const { name } = useParams();
  const [active, setActive] = useState(false);
  const [unactive, setUnActive] = useState(false);

  const [likeCount, setLikeCount] = useState(0);
  const [dislikeCount, setDislikeCount] = useState(0);

  const [activeBtn, setActiveBtn] = useState("none");

  const handleClick = () => {
    setActive(!active);
  };

  const handleUnClick = () => {
    setUnActive(!unactive);
  };

  const handleLikeClick = (likes, dislike) => {
    if (activeBtn === "none") {
      setLikeCount(likes + 1);
      setActiveBtn("like");
      return;
    }

    if (activeBtn === 'like') {
      setLikeCount(likes - 1);
      setActiveBtn("none");
      return;
    }

    if (activeBtn === "dislike") {
      setLikeCount(likes + 1);
      setDislikeCount(dislike - 1);
      setActiveBtn("like");
    }
  };

  const handleDisikeClick = (likes, dislike) => {
    if (activeBtn === "none") {
      setDislikeCount(dislike + 1);
      setActiveBtn("dislike");
      return;
    }

    if (activeBtn === 'dislike') {
      setDislikeCount(dislike - 1);
      setActiveBtn("none");
      return;
    }

    if (activeBtn === "like") {
      setDislikeCount(dislike + 1);
      setLikeCount(likes - 1);
      setActiveBtn("dislike");
    }
  };

  useEffect(() => {
    let uri = "posts/";
    if (postId) uri += postId;
    if (name) uri += "author/" + name;

    fetch(props.apiUrl + uri)
      .then((response) => response.json())
      .then((response) => {
        setPosts(response);
      });
  }, [postId, name]);

  return (
    <span key={name}>
      {posts.map((post) => (
        <section key={post._id.$oid}>
          <header className="main">
            <h1>{post.title}</h1>
          </header>
          <h3>{post.resume}</h3>
          <p>{post.text}</p>
          <div className="container">
            <div className="btn-container">
              <button
                className={`btn ${activeBtn === "like" ? "like-active" : ""}`}
                onClick={handleLikeClick(post)}
                Style={{ border: "1 px solid white" }}
              >
                <i class="fa fa-thumbs-up"></i>
                {likeCount}
              </button>

              <button
                className={`btn ${activeBtn === "dislike" ? "dislike-active" : ""}`}
                onClick={handleDisikeClick(post)}
                Style={{ border: "1 px solid white" }}
              >
                <i class="fa fa-thumbs-down"></i>
                {dislikeCount}
              </button>
            </div>
          </div>
          <h4>Related Links</h4>
          <ul className="alt">
            {post.relatedlinks.map((link, index) => (
              <li key={index}>{link}</li>
            ))}
          </ul>
          <h4>Tags</h4>
          <ul>
            {post.tags.map((tag, index) => (
              <li key={index}>{tag}</li>
            ))}
          </ul>
          <h4>Author</h4>
          <p>{post.author}</p>
        </section>
      ))}
      <p>
        <Link to="/">Back to Home Page</Link>
      </p>
    </span>
  );
}
